import { useRecoilState } from "recoil";
function Cart(props){
    const [currentCart,setCart]=useRecoilState(props.atom)
    let items=[]
    for (const id in currentCart){
        items.push(<div key={id} className='single-item-container'>
            <div className="cart-item-image">
                <img src={currentCart.id.image} alt='cart-image'/>
            </div>
            <div className="cart-item-description">
                <p><strong>{currentCart.id.name}</strong></p>
                <p>{currentCart.id.description}</p>
                <p>Quantity: {currentCart.id.quantity}</p>
                <p>Price: <strong>{currentCart.id.price*currentCart.id.quantity}</strong></p>
            </div>
        </div>)
    }
    return (
        <div className="all-items-container">
            {items}
        </div>
    )
}
export default Cart;
