import Card from './Card'
import Ribbon from './top-ribbon';
import './home-styling.css'
import { useQuery } from '@tanstack/react-query';
import axios from 'axios'

function useProducts() {
    return useQuery(["posts"],   async () => {
      const { data } = await axios.get(
        "https://obscure-refuge-62167.herokuapp.com/products"
      );
      return data;
    });
}

function Home(){
    const {data,status}=useProducts()

    if (status==='error')
        return <h1>Please try later, Server Down</h1>

    if (status==='loading' || status==='fetching')
        return <h1>Please wait, fetching data</h1>

    console.log(data.length)

    //for (let i=0;i<data.length;i++)
    //    cards.push(<div className='card' key={data[i]['id']}><Card data={data[i]} /></div>)
    let cards=[];
    for (let i=0;i<data.length;i++)
       cards.push(<div className='card' key={data[i]['id']}><Card data={data[i]} /></div>)
    return (
        <>
        <Ribbon/>
        <div className='cards-container'>
            {cards}
        </div>
        </>
    )
}
export default Home;
