import { useRecoilState } from "recoil";
import axios from "axios";
import {useQuery} from '@tanstack/react-query'
import ItemInCart from "./ItemInCart";
function Cart(props){
    const [currentCart,setCart]=useRecoilState(props.cartQuantity)
    return (
        <div className="all-items-container">
            {Object.keys(currentCart).map((id)=><ItemInCart id={id} quantity={currentCart}/>)}
        </div>
    )
}
export default Cart;
