import Card from './Card'
import Ribbon from './top-ribbon';
import './home-styling.css'
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
import {useNavigate} from 'react-router-dom'

function useProducts() {
    return useQuery(["posts"],   async () => {
      const { data } = await axios.get(
        "https://obscure-refuge-62167.herokuapp.com/products"
      );
      return data;
    });
}

function Home(){
    const navigate=useNavigate();
    const {data,status,isFetching}=useProducts()

    if (status==='error')
        return <h1>Please try later, Server Down</h1>

    if (status==='loading' || isFetching)
        return <h1>Please wait, fetching data</h1>

    let cards=[];
    for (let i=0;i<data.length;i++)
       cards.push(<button onClick={()=>{navigate(`products/${data[i]['id']}`)}} className='card' key={data[i]['id']}><Card data={data[i]} /></button>)
    return (
        <>
        <Ribbon/>
        <div className='cards-container'>
            {cards}
        </div>
        </>
    )
}
export default Home;
