import './product-page-styling.css'
import { useQuery } from '@tanstack/react-query';
import axios from 'axios'
import {useState,useEffect} from 'react'

function useProducts() {
    return useQuery(["posts"],   async () => {
      const { data } = await axios.get(
        "https://obscure-refuge-62167.herokuapp.com/products/1"
      );
      return data;
    });
}

function Product(){
    const [product,setProduct]=useState({image:null,name:'',description:'',price:0,quantity:0});

    const {data,status}=useProducts();

    useEffect(()=>{
        setProduct({
            image:data['image'],
            name:data['name'],
            description:data['description'],
            price:data['price'],
            quantity:data['quantity']
        })
    },[product]);

    if (status==='loading')
        return <h1>Loading...</h1>

    return (
        <div className='product-page-container'>
            <div className='product-page-image'>
                <img className='card-image' src={product['image']} alt="bottles"/>
            </div>
            <div className='product-page-product-description'>
                <h2>{product['name']}</h2>
                <p>{product['description']}</p>
                <p className='product-page-price'>Price : <span>{`$ ${product['price']}`}</span></p>
                <p>Quantity : {`${product['quantity']}`}</p>
                <button className='add-to-cart-button'>Add to Cart</button>
            </div>
        </div>
    )
}
export default Product;
