import './product-page-styling.css'
import { useQuery } from '@tanstack/react-query';
import axios from 'axios'
import {useParams} from 'react-router-dom';
import {useState,useEffect} from 'react'
function useProducts(productId=1) {
    if (productId===':id'){
        productId=1;
    }
    return useQuery(["products"],   async () => {
      const { data } = await axios.get(
        `https://obscure-refuge-62167.herokuapp.com/products/${productId}`
      );
      return data;
    });
} //remove 

function buttonStyle(bgcolor){
    return {
        backgroundColor:bgcolor,
    }
}

function Product(){
    const params=useParams();
    const {data,status,isFetching}=useProducts(params['id']);

    const [currentImage,setCurrentImage]=useState('')
    useEffect(()=>{
        if (data){
            setCurrentImage(data?.image)
        }
    },[data])
    if (status==='error')
        return <h1>Server Down</h1>
    if (status==='loading' || isFetching)
        return <h1>Loading...</h1>


    //setting the text for Quantity
    let quantity_text='';
    if (data['quantity']>10)
        quantity_text='Available'
    else if (data['quantity']===0)
        quantity_text='Unavailable'
    else if (data['quantity']<=10)
        quantity_text='Selling Fast ⚡⚡⚡'

    let variants=[]
    //useEffect
    // for(let i=0;i<data['variants'].length;i++){
    //     variants.push(<button className='variant-buttons' onClick={()=>{ setProduct({image:data['variants'][i]['image']})} } style={buttonStyle(data['variants'][i]['color'])}></button>)
    // }
    return (
        <div className='product-page-container'>
            <div className='product-page-image'>
                <img className='card-image' src={currentImage} alt="bottles"/>
            </div>
            <div className='product-page-product-description'>
                <h2>{data['name']}</h2>
                <p>{data['description']}</p>
                <p className='product-page-price'>Price : <span>{`$ ${data['price']}`}</span></p>
                <p>Quantity : {`${quantity_text}`}</p>
                {data?.variants.map((variant,index)=>{
                    return <button className='variant-buttons' style={{backgroundColor : variant.col}} onClick={()=>{ setCurrentImage(variant?.image) }}></button>
                })}
                <br/>
                <button className='add-to-cart-button'>Add to Cart</button>
            </div>
        </div>
    )

}
export default Product;
