import axios from "axios";
import {useQuery} from '@tanstack/react-query';
function ItemInCart(props){
    const {data,status,isFetching}=useQuery(["products"], async () => {const { data } = await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${props.id}`);
                                                                    return data;}
                                            );
        if (status==='error' || status==='loading' || isFetching){
            return <h3>AAPKO</h3>
        }
        return (
            <div key={props.id} className='single-item-container'>
                <div className="cart-item-image">
                    <img src={data.image} alt='cart-item'/>
                </div>
                <div className="cart-item-description">
                    <p><strong>{data.name}</strong></p>
                    <p>{data.description}</p>
                    <p>Quantity: {data.quantity}</p>
                    <p>Price: <strong>{data.price*props.quantity}</strong></p>
                </div>
            </div>
        )
}
export default ItemInCart;
