import { useRecoilState } from "recoil";
function Cart(props){
    const [currentCart,setCart]=useRecoilState(props.cartQuantity)
    let items=[]
    for (const id in currentCart){
        const {data,status,isFetching}=useQuery(["products"], async () => {const { data } = await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
                                                                    return data;}
                                            );
        if (status==='error' || status==='loading' || isFetching){
            items.push(<h3>Aapko</h3>)
            continue
        }
        items.push(<div key={id} className='single-item-container'>
            <div className="cart-item-image">
                <img src={data.image} alt='cart-item'/>
            </div>
            <div className="cart-item-description">
                <p><strong>{data.name}</strong></p>
                <p>{data.description}</p>
                <p>Quantity: {data.quantity}</p>
                <p>Price: <strong>{data.price*currentCart.id.quantity}</strong></p>
            </div>
        </div>)
    }
    return (
        <div className="all-items-container">
            {items}
        </div>
    )
}
export default Cart;
