import {NavLink} from 'react-router-dom'
import './navbar-styling.css'
import {useRecoilState} from 'recoil'
import Cart from './Cart'

function Navbar(props){

    const [currentCart,setCart]=useRecoilState(props.atom)
    function renderCart(data){
        let items=Object.keys(data).map((id)=>{
            return (<div>
                <p>{id}:{currentCart[id]}</p>
            </div>)
        })
        return items;
    }
    return (
        <div className='nav-container'>
            <div className='navbar-container'>
                <NavLink to='/'>Home</NavLink>
                <NavLink to='/products/:id'>Product</NavLink>
            </div>
            <div>
                <button className='cart-button'>{`Cart(${ Object.keys(currentCart).length})`}</button>
                {/* <div className='all-cart-items'>
                    <Cart cartQuantity={props.atom}/>
                </div> */}
                <div className="all-items-container">
            {Object.keys(currentCart).map((id)=><ItemInCart key={id} id={id} quantity={currentCart[`${id}`]} /> )}
        </div>
            </div>
        </div>
    )
}
export default Navbar;
